.social_icon{
    li {
        display: inline-block;
        margin-right: 20px;
        a {
            height: 40px;
            width: 40px;
            text-align: center;
            border: 1px solid #677fb5;
            display: block;
            line-height: 40px;
            font-size: 18px;
            border-radius: 5px;
            @include transition;
            color: #677fb5;
        }
        :nth-child(2){
            a{
                color: #70c2e9;
                border-color: #70c2e9;
            }
            &:hover{
                a{
                    background: #70c2e9;
                }
            }
        }
        :nth-child(3){
            a{
                color: #895a4d;
                border-color: #895a4d;
            }
            :hover{
                a{
                    background: #895a4d;
                }
            }
        }
        :nth-child(4){
            a{
                color: #d34836;
                border-color: #d34836;
            }
            :hover{
                a{
                    background: #d34836;
                }
            }
        }
        :nth-child(5){
            a{
                color: #007ab9;
                border-color: #007ab9;
            }
            :hover{
                a{
                    background: #007ab9;
                }
            }
        }
        :nth-child(6){
            a {
                color: #d8545d;
                border-color: #d8545d;
            }
            :hover{
                a{
                    background: #d8545d;
                }
            }
        
        :nth-child(7){
            a{
                color: #5ecbf3;
                border-color: #5ecbf3;
            }
            :hover{
                a{
                    background: #5ecbf3;
                }
            }
        }
        :nth-child(8){
            a{
                color: #ff3ba4;
                border-color: #ff3ba4;
            }
            &:hover{
                a{
                    background: #ff3ba4;
                }
            }
        }
        :last-child{
            margin-right: 0px;
            a{
                color: #faaa5e;
                border-color: #faaa5e;
            }
            :hover{
               a{
                    background: #faaa5e;
                }
            }
        }
        :hover{
            a{
                background: #677fb5;
                color: #fff;
            }
        }
    }
}

.education_area{
    padding-bottom: 60px;
    education_inner_area{
        position: relative;
        overflow: hidden;
